import React, { Component } from "react";

class Detail extends Component{
    componentDidMount(){
        const {location,history} = this.props;
        if (location.state === undefined){
            history.push("/");
        }
    }
    render(){
        const {state} = this.props.location;
        if (state){
            return (
                <div>
                    <img src={state.poster}/>
                </div>
            )
        }else{
            return null;
        }
    }
}

export default Detail;
