import React, { Component } from "react";

class App extends Component{
    state={
        isLoading:true,
        movies:[]
    };

    render(){
        const {isLoading} = this.state;
        return (
        <div class="container">
            {isLoading 
            ? 
            <div className="loader">
                <span className="loader-loading">Loading...</span>
            </div> 
            : "Ready"
            }
        </div>

        )
    }
}

export default App;
